    @Test
    void givenValidDnsMessageRequest_whenUnpacked_thenExpectValidDnsMessage() {
        // Arrange
        byte[] data = getRequestData();

        // Act
        DnsMessage response = parser.unpackRequest(data);

        // Assert
        assertNotNull(response);
    }

    @Test
    void givenValidDnsMessageRequest_whenUnpacked_thenExpectValidDnsMessageHeader() {
        // Arrange
        byte[] data = getRequestData();

        // Act
        DnsMessage response = parser.unpackRequest(data);
        DnsHeader header = response.header();

        // Assert
        assertNotNull(header);
        assertEquals((short)61933, header.ID());
        assertEquals(288, header.flags());
        assertEquals(1, header.QDCOUNT());
        assertEquals(0, header.ANCOUNT());
        assertEquals(0, header.NSCOUNT());
        assertEquals(1, header.ARCOUNT());
    }

    @Test
    void givenValidDnsMessageRequest_whenUnpacked_thenExpectValidDnsMessageQuestion() {
        // Arrange
        byte[] data = getRequestData();

        // Act
        DnsMessage response = parser.unpackRequest(data);
        DnsQuestion question = response.questions()[0];

        // Assert
        assertNotNull(question);
        assertEquals("www.google.com", question.QNAME());
        assertEquals(1, question.QTYPE());
        assertEquals(1, question.QCLASS());
    }

    @Test
    void givenValidDnsMessageResponse_whenUnpacked_thenExpectValidDnsMessageAnswer() {
        // Arrange
        byte[] data = getResponseData();

        // Act
        DnsMessage response = parser.unpackRequest(data);
        DnsResourceRecord answer = response.answers()[0];

        // Assert
        assertNotNull(answer);
        assertEquals("www.google.com", answer.NAME());
        assertEquals(1, answer.TYPE());
        assertEquals(1, answer.CLASS());
        assertEquals(175, answer.TTL());
        assertEquals("216.58.212.228", answer.RDATA());
    }
